import React, { useState, useEffect } from "react";
import axios from 'axios';
import { Link, useParams, useLocation, useNavigate } from "react-router-dom";
import { Form, Button, Col, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../components/Loader";
import Message from "../components/Message";
import FormContainer from "../components/FormContainer";
import { register } from "../actions/userActions";
import { listPostDetails, updatePost } from "../actions/postActions";
import { POST_UPDATE_RESET } from '../constants/postConstants'

function PostEditScreen() {

    const { id } = useParams();
    const postId = id;
    const history = useNavigate();
    const location = useLocation();
  
    const [formData, setFormData] = useState({
        title: "",
        description: "",
        image: "",
        category:"",
    });

    const [errors, setErrors] = useState({
        title: "",
        description: "",
        image: "",
        category:"",
    });

    const handleChange = ({ currentTarget: input }) => {
        let newData = { ...formData };
        newData[input.name] = input.value;
        setFormData(newData);
    };

    const handleImageChange = (e) => {
        let newData = { ...formData };
        newData["image"] = e.target.files[0];
        
        setFormData(newData);
    };

    const dispatch = useDispatch();

    const postDetails = useSelector((state) => state.postDetails);
    const { error, loading, post } = postDetails;

    const postUpdate = useSelector((state) => state.postUpdate);
    const { error:errorUpdate, loading:loadingUpdate, success: successUpdate } = postUpdate;

    const doSubmit = async (e) => {        
        e.preventDefault();
        if(formData.title.trim().length === 0){
            setErrors({
                "title": [
                    "Title field is required."
                ]
            })
        }else if(formData.image.length === 0){
            setErrors({
                "image": [
                    "Image field is required."
                ]
            })
        }
        else if(formData.description.trim().length === 0){
            setErrors({
                "description": [
                    "Description field is required."
                ]
            })
        }
        else if(formData.category.trim().length === 0){
            setErrors({
                "category": [
                    "Category field is required."
                ]
            })
        }       
        else{
            try{
                await dispatch(updatePost(
                    formData,
                    
                ));
            } catch (error){
                console.log(error)
            }

        }
              
    };


    useEffect(() => {         
        if(successUpdate){
            dispatch({type: POST_UPDATE_RESET})
            history('')
        }else{
          if (!post.name || post._id !== Number(postId)) {
            dispatch(listPostDetails(postId));
            } else {
                setFormData(post.formData)
            } 
        }     
    }, [dispatch, post, postId, history, successUpdate]);

    return (
        <FormContainer>
            <ToastContainer/>
            {successUpdate ? ('Success') : (
                <span>{errorUpdate}</span>
            )}
            <h4 className="text-center">Create Post</h4>
            <Form className="createLabel" validated>
                <Row>
                    <Form.Group className="mb-3" controlId="titleInput">
                        <Form.Label>Category</Form.Label>
                        <Form.Control
                            required
                            as="select"
                            name="category"
                            value={formData.category ? formData.category : '' }
                            isInvalid={errors.category}
                            onChange={(e) => {
                                handleChange(e);
                            }}
                        >
                       
                        {
                            categories.map((c) => 
                               
                                <option key={c._id} value={c._id}>
                                    {c.name}
                                </option>
                            )
                        }
                        {errors.title && (
                            <Form.Text className="errMsg text-danger">
                                    {errors.title}
                            </Form.Text>
                        )}
                        </Form.Control>
                    </Form.Group>
                </Row>
                <Row>
                    <Form.Group className="mb-3" controlId="titleInput">
                        <Form.Label>Title</Form.Label>
                        <Form.Control
                            required
                            type="text"
                            name="title"
                            placeholder="Type Your Post Title"
                            value={formData.title}
                            isInvalid={errors.title}
                            onChange={(e) => {
                                handleChange(e);
                            }}
                        />
                        {errors.title && (
                            <Form.Text className="errMsg text-danger">
                                    {errors.title}
                            </Form.Text>
                        )}
                    </Form.Group>
                </Row>
                <Row>
                    <Form.Group controlId="formFile" className="mb-3">
                        <Form.Label>Image</Form.Label>
                        <Form.Control
                            id = "myFile"
                            required
                            type="file"
                            name="image"
                            accept="image/jpeg,image/png,image/gif"
                            onChange={(e) => {
                                handleImageChange(e);
                            }}
                        />
                        {errors.image && (
                            <Form.Text className="errMsg text-danger">
                                {errors.image}
                            </Form.Text>
                        )}
                    </Form.Group>
                </Row>
                <Form.Group className="mb-3" controlId="descriptionInput">
                    <Form.Label>Description</Form.Label>
                    <Form.Control
                        as="textarea"
                        required
                        rows={10}
                        name="description"
                        placeholder="Type Description"
                        value={formData.description}
                        isInvalid={errors.description}
                        onChange={(e) => {
                            handleChange(e);
                        }}
                    />
                    {errors.description && (
                        <Form.Text className="errMsg text-danger">
                            {errors.description}
                        </Form.Text>
                    )}
                </Form.Group>
                <div className="btn-login">
                    <Button
                        variant="primary"
                        type="submit"
                        onClick={(e) => doSubmit(e)}
                        >
                        Submit
                    </Button>
                </div>
                
        </Form>
        </FormContainer>
    )
}

export default PostEditScreen
