import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Button, Form, } from "react-bootstrap";
import { Link, useParams, useNavigate } from "react-router-dom";
import { createPostLike, listPostDetails, listPosts } from "../actions/postActions";
import { POST_CREATE_LIKE_RESET } from '../constants/postConstants'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Reactions({value, likes, comment, post, userLike, user, ActualPost}) {

    const { id } = useParams();
    const history = useNavigate();
    const dispatch = useDispatch();

    let [numLikes, setnumLikes] = useState(0);

    const [displayConfirmationModal, setDisplayConfirmationModal] = useState(false);
    const [message, setMessage] = useState(null);
    const [pid, setPid] = useState(null);

    const showDeleteModal = (pid) => {
        setCid(pid);
        setMessage(`Are you sure you want to delete this post '${ActualPost.find((x) => x._id === pid).title}' ? `);

        setDisplayConfirmationModal(true)
    };

    const hideConfirmationModal = () => {
        setDisplayConfirmationModal(false);
        
    }

    const submitPostDeleteHandler = (pid) =>{
        dispatch(deletePost(pid));
        setDisplayConfirmationModal(false);
    }

    const postLikes = useSelector((state) => state.postLikes);
    const { loading, error,  success: successLike } = postLikes;

    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;

    useEffect(() => {
        if(successLike){
            setnumLikes(0)          
        }   
      }, [dispatch, post, successLike]);


    const submitLikeHandler = (e) =>{
        e.preventDefault()
        dispatch(createPostLike(
          post,{
            numLikes
          }
        ))
    }

    return (
        <div className="reactions">
            <ToastContainer />
            {userInfo ? (
                
                <span className="likeAction">
                    <Link onClick={submitLikeHandler}>
                        <span>
                            <i
                            style={{ color:"#e60000" }}
                            className={
                                value >= 1
                                ? "fa fa-heart"
                                : value == 0
                                ? "fa-regular fa-heart"                   
                                : "far fa-heart-o"
                            }
                            ></i>
                            {likes}
                            

                        </span> 
                    </Link>
                </span>
                
            ):(
                <span className='reaction'>
                    <Link to='/login'>
                        <i
                        style={{ color:"#e60000" }}
                        className={
                            value >= 1
                            ? "fa fa-heart"
                            : value == 0
                            ? "fa-regular fa-heart"                   
                            : "far fa-heart-o"
                        }
                        ></i>
                        {likes}
                    </Link>
                   
                </span> 
            )}     
            
             <span className="reaction">
                 <Link to={`/post/${post}`}>
                    <i
                    style={{ color:"" }}
                    className={"fa fa-comment"}
                    ></i>
                    {comment}
                 </Link>
               
            </span>


            <span>
                <i
                style={{ color:"" }}
                className={"fa fa-share"}
                ></i>
            </span>
            <span>  </span>
            <span>  </span>
            {userInfo && userInfo._id === user ? (
                <small className='reactionDel'>
                    <Link >
                        <i className="fas fa-trash danger cursor"></i>
                    </Link>
                    
                </small> 
            ):(
                <span> </span>
            )} 
            

        </div>
    )
}

export default Reactions
