import {axiosInstance, axios} from "axios";

const apiSettings = {
    createMyModelEntry: async (data) => {
        let form_data = new FormData();
        if (data.image)
            form_data.append("image", data.image, 
            data.image_url.name);
        form_data.append("title", data.title);
        form_data.append("description", data.description);
        form_data.append("category", data.category);

        try{
            const config = {
              headers:{
                'Content-Type': 'multipart/form-data'
              }
            }
            const {data} = await axios.post('/api/products/upload/', formData, config)
            

        }catch(error){
            
        }
    
    },

    createListing: async (data) => {
        let form_data = new FormData();
        if (data.image)
            form_data.append("image", data.image, data.image.name);
        form_data.append("title", data.title);
        form_data.append("description", data.description);
        form_data.append("category", data.category);


    const myNewModel = await axiosInstance
            .post(`mymodels/`, form_data, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            }).then((res) => {
                return res;
            }).catch((error) => {
                return error.response;
            });

        if (myNewModel.status === 201) {
            window.location.href = `/mymodels/${myNewModel.data.id}`;
        }
        return myNewModel;
    },

   
};

export default apiSettings;
