import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Jumbotron from '../components/Jumbotron'
import Loader from '../components/Loader'
import Message from '../components/Message'
import { Link } from 'react-router-dom'
import { useNavigate, useLocation } from "react-router-dom";
import { listPosts } from '../actions/postActions'

function HomeScreen() {
    
    const dispatch = useDispatch();   
    const history = useNavigate();
    const location = useLocation();

    const postList = useSelector((state) => state.postList);
    const { error, loading, posts } = postList;

    useEffect(() => {
        dispatch(listPosts());
      }, [ dispatch]);

    return (
        <div className="row">
             <Jumbotron/>
             {loading?(
                    <Loader/>
                ) : error ? (
                    <Message variant="danger">{error}</Message>
                ) : (
                    <div>
                       
                        <div className="col-md-8">
                            <h1>All POSTS</h1>
                            <div className="col-md-12">
                                <div className="row g-0 border rounded overflow-hidden flex-md-row mb-4 shadow-sm h-md-250 position-relative">
                                    <div className="col p-4 d-flex flex-column position-static">
                                        <strong className="d-inline-block mb-2"> <Link className="text-success" to=""></Link></strong>
                                        <h3 className="mb-0"><Link className="article-title stretched-link" to=""></Link></h3>
                                        <div className="mb-1 text-muted"></div>

                                        <p className="mb-auto">                            
                                            <Link className="btn btn-outline-secondary btn-sm mt-1 mb-1" to="">Read More</Link>                             
                                        </p>
                                        <img className="rounded-circle article-img" src=""/>
                                    </div>
                                    <div className="col-auto d-none d-lg-block">
                                        <img className="" src=""/>
                                    </div>
                                </div>
                            </div>
                        </div>

                        <div className="col-md-4">
                            <div className="content-section">
                                <h3>Trending POST</h3>
                                <p className="muted"> You can put any information here you'd like.
                                    <ul className="list-group">
                                        
                                        <Link to="">
                                            <li className="list-group-item list-group-item-light"></li>
                                        </Link>
                                        
                                    </ul>
                                </p>
                            </div>
                        </div>

                    </div>
                )
                }           
        </div>
    )
}

export default HomeScreen
