import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Button, Form, } from "react-bootstrap";
import { Link, useParams, useNavigate } from "react-router-dom";
import { createPostLike } from "../actions/postActions";
import { POST_CREATE_LIKE_RESET } from '../constants/postConstants'


function Reactions({value, likes, comment, post}) {

    const { id } = useParams();
    const history = useNavigate();
    const dispatch = useDispatch();

    const [numLikes, setnumLikes] = useState(0);

    const postLikes = useSelector((state) => state.postLikes);
    const { loading, error, like } = postLikes;

    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;

    const submitHandler = (e) =>{
        e.preventDefault()
        dispatch(createPostLike(
          id,{
            numLikes
          }
        ))
  }

    return (
        <div className="reactions">

            {userInfo? (
                <Link onClick={submitHandler} className="reaction">
                    <span>
                        <i
                        style={{ color:"#e60000" }}
                        className={
                            value >= 1
                            ? "fa fa-heart"
                            : value == 0
                            ? "fa-regular fa-heart"                   
                            : "far fa-heart-o"
                        }
                        ></i>
                        {likes}
                    </span> 
                </Link>
            ):(
                <span>
                    <i
                    style={{ color:"#e60000" }}
                    className={
                        value >= 1
                        ? "fa fa-heart"
                        : value == 0
                        ? "fa-regular fa-heart"                   
                        : "far fa-heart-o"
                    }
                    ></i>
                    {likes}
                </span> 
            )}     
            
             <span className="reaction">
                 <Link to={`/post/${post}`}>
                    <i
                    style={{ color:"" }}
                    className={"fa fa-comment"}
                    ></i>
                    {comment}
                 </Link>
               
            </span>


            <span>
                <i
                style={{ color:"" }}
                className={"fa fa-share"}
                ></i>
            </span>
        </div>
    )
}

export default Reactions
