import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { LinkContainer } from "react-router-bootstrap";
import { Link } from 'react-router-dom'
import { Table, Button, Row, Col, Image } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../components/Loader";
import Message from "../components/Message";
import Paginate from "../components/Paginate";
import {  deletePostComment, listComments } from "../actions/postActions";
import { POST_LIST_COMMENTS_RESET } from '../constants/postConstants'
import ConfirmationModal from '../components/ConfirmationModal'

function CommentsListScreen() {
    const history = useNavigate();
    const location = useLocation();
    const dispatch = useDispatch();

    const [displayConfirmationModal, setDisplayConfirmationModal] = useState(false);
    const [message, setMessage] = useState(null);
    const [cid, setCid] = useState(null);

    const postList = useSelector((state) => state.postList);
    const { error:postError, loading:postLoading, posts } = postList;

    const commentList = useSelector((state) => state.commentList);
    const { error, loading, comments } = commentList;

    const postCommentDelete = useSelector((state) => state.postCommentDelete);
    const {
      loading: loadingDelete,
      error: errorDelete,
      success: successDelete,
    } = postCommentDelete;

    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;

    const [searchField, setSearchField] = useState("");
    const [searchShow, setSearchShow] = useState(false);

    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(5);

    const [pageNumberLimit, setPageNumberLimit] = useState(5);
    const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(5);
    const [minPageNumberLimit, setminPageNumberLimit] = useState(0);


    const handleClick= (event) =>{
        setCurrentPage(Number(event.target.id));
    }
    const handleNextBtn = () => {
        setCurrentPage(currentPage + 1);

        if(currentPage + 1 > maxPageNumberLimit){
            setmaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
            setminPageNumberLimit(minPageNumberLimit + pageNumberLimit);
        }
    }
    const handlePrevBtn = () => {
        setCurrentPage(currentPage - 1);

        if((currentPage - 1) % pageNumberLimit == 0 ){
            setmaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
            setminPageNumberLimit(minPageNumberLimit - pageNumberLimit);
        }
    }

    const pages = [];

    for(let i = 1; i<=Math.ceil(comments.length/itemsPerPage); i++){
        pages.push(i);
    }

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = posts.slice(indexOfFirstItem, indexOfLastItem);

    const renderPageNumbers = pages.map((number) => {
        if(number < maxPageNumberLimit + 1 && number > minPageNumberLimit){
            return(
                <li 
                    className={`page-link ${currentPage === number ? "active" : null}`}
                    key={number} 
                    id={number} 
                    onClick={handleClick}
                >
                  {number} 
                </li>
            )
        }else{
            return null;
        }
        
    })

    let pageIncrementBtn = null;
    if(pages.length > maxPageNumberLimit){
        pageIncrementBtn =  <li onClick={handleNextBtn}>
                                <button 
                                    class="page-link"  
                                    aria-label="Next"
                                    disabled={currentPage == pages[pages.length - 1] ? true : false}
                                    >
                                    <span aria-hidden="true">&hellip;</span>
                                </button>
                            </li>
    }

    let pageDecrementBtn = null;
    if(pages.length > maxPageNumberLimit){
        pageDecrementBtn =  <li onClick={handlePrevBtn}>
                                    <button class="page-link" 
                                            aria-label="Previous"
                                            disabled={currentPage == pages[0] ? true : false}
                                            >
                                        <span aria-hidden="true">&hellip;</span>
                                    </button>
                                </li>
    }
    const filteredComments = comments.filter(
        x => {
          return (
            x
            .comment
            .toLowerCase()
            .includes(searchField.toLowerCase()) ||
            x
            .users
            .toLowerCase()
            .includes(searchField.toLowerCase()) ||
            x
            .posts
            .toLowerCase()
            .includes(searchField.toLowerCase())
          );
        }
      );
      const handleSearch = e => {
        setSearchField(e.target.value);
        if(e.target.value===""){
          setSearchShow(false);
        }
        else {
          setSearchShow(true);
        }
      };


    useEffect(() => {
        dispatch({type: POST_LIST_COMMENTS_RESET})
    
        if(!userInfo.isAdmin){
          history("/login")
        }else{
          dispatch(listComments());
        }
      }, [dispatch, history, userInfo, successDelete]);

    const showDeleteModal = (cid) => {
        setCid(cid);
        setMessage(`Are you sure you want to delete this comment '${comments.find((x) => x._id === cid).comment}' ? `);        
        setDisplayConfirmationModal(true)
    };

    const hideConfirmationModal = () => {
        setDisplayConfirmationModal(false);
        
    }

    const deleteHandler = (cid) => {       
        dispatch(deletePostComment(cid));
        setDisplayConfirmationModal(false);
    };

    return (
        <div>
            <Row className="justify-content-md-center">
              <Col>
                <h1>Comments</h1>
              </Col>
             
            </Row>
          {loading ? (
            <Loader />
          ) : error ? (
            <Message variant="danger">{error}</Message>
          ) : (
            <Table striped bordered hover responsive className="table-sm">
              <thead>
                <tr>
                  <th>POST TITLE</th>
                  <th>USER</th>
                  <th>COMMENT</th>
                  <th>ACTIONS</th>
                </tr>
              </thead>
              <tbody>
                {comments.map((comment) => (
                  <tr key={comment._id}>
                    <td>{comment.posts}</td>
                    <td>{comment.users}</td>
                    <td>{comment.comment}</td>
                    <td>
                      <Button
                        className="btn-sm"
                        variant="danger"
                        onClick={() => showDeleteModal(comment._id)}
                      >
                        <i className="fas fa-trash"></i>
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
          <ConfirmationModal variantBtnDel={"danger"} ModalTitle={"Confirmation"} showModal={displayConfirmationModal} confirmModal={deleteHandler} hideModal={hideConfirmationModal}  cid={cid} message={message}  />
        </div>
    );
}

export default CommentsListScreen
