import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
    Row,
    Col,
    Image,
    ListGroup,
    Button,
    Card,
    Form,
  } from "react-bootstrap";
import { Link, useParams, useNavigate } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import Trending from '../components/Trending'
import { listPostDetails } from "../actions/postActions";
import Reactions from '../components/Reactions'
import moment from 'moment';


function PostScreen() {
    const { id } = useParams();
    const history = useNavigate();
    const dispatch = useDispatch();

    const postDetails = useSelector((state) => state.postDetails);
    const { loading, error, post } = postDetails;

    useEffect(() => {
        dispatch(listPostDetails(id));
      }, [dispatch, id]);
    

    return (
        <div>
            <Link to="/" className="btn btn-light my-3">
                <i className="fa fa-home"></i>
            </Link>
            {loading ? (
                <Loader/>
            ) : error ? (
                <Message variant="danger">{error}</Message>
            ) :(
                <div>
                    <Row>
                        <Col md={4}>
                            <Image src={post.image} alt={post.title} fluid />
                        </Col>
                        <Col md={5}>
                            <ListGroup variant="flush">
                                <ListGroup.Item>
                                    <p className="text-muted">
                                        <span className="badge bg-dark">
                                            {post.categories}
                                        </span>
                                    </p>
                                    <h3>{post.title}</h3>                                    
                                </ListGroup.Item>
                               

                                <ListGroup.Item>
                                    {post.description}
                                </ListGroup.Item>

                                <ListGroup.Item>
                                        <Reactions value={0} likes={post.likes.length} comment={post.comments.length} post={post._id}/>
                                </ListGroup.Item>

                            </ListGroup>
                        </Col>
                        <br/>
                        <Col md={3}>
                            <Trending/>
                        </Col>
                    </Row>

                    <br/>
                    <Row>
                        <Col md={6}>
                            {post.comments.length === 0 && <Message variant='info'>No Comments</Message>}
                            <ListGroup>
                                {post.comments.map((comment) => (
                                    <ListGroup.Item key={comment._id}>
                                        <div className="d-flex mt-2 align-items-center">
                                            <strong>{comment.name}</strong> - <p>{moment(comment.createdAt).fromNow()}</p> 
                                        </div>                                      
                                        <p>{comment.createdAt.substring(0,10)}</p>
                                        <p>{comment.comment}</p>
                                    </ListGroup.Item>
                                ))}
                            </ListGroup>
                        </Col>
                    </Row>
                </div>
            )

            }
            
        </div>
    )
}

export default PostScreen
