import React from 'react'

function Reactions({value, color, comment}) {
    return (
        <div className="reactions">
             <span>
                {/*this code is a conditional statement to display a like.*/}
                <i
                style={{ color:"#e60000" }}
                className={
                    value >= 1
                    ? "fa fa-heart"
                    : value == 0
                    ? "fa-regular fa-heart"                   
                    : "far fa-heart-o"
                }
                ></i>
            </span>
           
            
             <span>
                <i
                style={{ color:"" }}
                className={"fa fa-comment fa-flip"}
                ></i>
            </span>

            <span>
                <i
                style={{ color:"" }}
                className={"fa fa-share"}
                ></i>
            </span>
        </div>
    )
}

export default Reactions
