import { 
    POST_LIST_REQUEST,
    POST_LIST_SUCCESS,
    POST_LIST_FAIL,

    POST_DETAILS_REQUEST,
    POST_DETAILS_SUCCESS,
    POST_DETAILS_FAIL,
    POST_DETAILS_RESET,

    POST_CREATE_COMMENT_REQUEST,
    POST_CREATE_COMMENT_SUCCESS,
    POST_CREATE_COMMENT_FAIL,
    POST_CREATE_COMMENT_RESET,

    POST_CREATE_LIKE_REQUEST,
    POST_CREATE_LIKE_SUCCESS,
    POST_CREATE_LIKE_FAIL,
    POST_CREATE_LIKE_RESET,

    POST_TRENDING_REQUEST,
    POST_TRENDING_SUCCESS,
    POST_TRENDING_FAIL,

    POST_DELETE_REQUEST,
    POST_DELETE_SUCCESS,
    POST_DELETE_FAIL,

    POST_CREATE_REQUEST,
    POST_CREATE_SUCCESS,
    POST_CREATE_FAIL,
    POST_CREATE_RESET,

    CATEGORY_LIST_REQUEST,
    CATEGORY_LIST_SUCCESS,
    CATEGORY_LIST_FAIL,
    CATEGORY_LIST_RESET,

    // POST_CREATE_LIKE_UPDATE_REQUEST,
    // POST_CREATE_LIKE_UPDATE_SUCCESS,
    // POST_CREATE_LIKE_UPDATE_FAIL,
    // POST_CREATE_LIKE_UPDATE_RESET,

 } from '../constants/postConstants'


export const postListReducer = (state = { posts:[]}, action) => {
    switch (action.type) {
      case POST_LIST_REQUEST:
        return {
          loading: true,
          ...state,
        };
      case POST_LIST_SUCCESS:
        return {
          loading: false,
          posts: action.payload,

        };
      case POST_LIST_FAIL:
        return {
          loading: false,
          error: action.payload,
        };
  
      default:
        return state;
    }
  };

  export const postDetailsReducer = ( state = { post: { comments: [], likes:[] } }, action) => {
    switch (action.type) {
      case POST_DETAILS_REQUEST:
        return { loading: true, ...state };
      case POST_DETAILS_SUCCESS:
        return { loading: false, post: action.payload };
      case POST_DETAILS_FAIL:
        return { loading: false, error: action.payload };
      case POST_DETAILS_RESET:
        return {};
      default:
        return state;
    }
  };
  
  export const postCommentCreateReducer = (state = {}, action) => {
    switch (action.type) {
      case POST_CREATE_COMMENT_REQUEST:
        return { loading: true };
      case POST_CREATE_COMMENT_SUCCESS:
        return { loading: false, success: true };
      case POST_CREATE_COMMENT_FAIL:
        return { loading: false, error: action.payload };
      case POST_CREATE_COMMENT_RESET:
        return {};
      default:
        return state;
    }
  };

  export const postLikeCreateReducer = (state = {}, action) => {
    switch (action.type) {
      case POST_CREATE_LIKE_REQUEST:
        return { loading: true };
      case POST_CREATE_LIKE_SUCCESS:
        return { loading: false, success: true };
      case POST_CREATE_LIKE_FAIL:
        return { loading: false, error: action.payload };
      case POST_CREATE_LIKE_RESET:
        return {};
      default:
        return state;
    }
  };

  export const postDeleteReducer = (state = {}, action) => {
    switch (action.type) {
      case POST_DELETE_REQUEST:
        return { loading: true };
      case POST_DELETE_SUCCESS:
        return { loading: false, success: true };
      case POST_DELETE_FAIL:
        return { loading: false, error: action.payload };
      default:
        return state;
    }
  };

  
  export const postTrendReducer = (state = {posts:[]}, action) => {
    switch (action.type) {
      case POST_TRENDING_REQUEST:
        return { loading: true, posts:[] };
      case POST_TRENDING_SUCCESS:
        return { loading: false, posts: action.payload };
      case POST_TRENDING_FAIL:
        return { loading: false, error: action.payload };
      default:
        return state;
    }
  };

  export const postCreateReducer = (state = {post: { categories: []}}, action) => {
    switch (action.type) {
      case POST_CREATE_REQUEST:
        return { loading: true };
      case POST_CREATE_SUCCESS:
        return { loading: false, success: true, post: action.payload };
      case POST_CREATE_FAIL:
        return { loading: false, error: action.payload };
      case POST_CREATE_RESET:
        return {};
      default:
        return state;
    }
  };


  export const listCategoryReducer = (state =  { categories: []}, action) => {
    switch (action.type) {
      case CATEGORY_LIST_REQUEST:
        return { loading: true, categories: [] };
      case CATEGORY_LIST_SUCCESS:
        return { loading: false, success: true, categories: action.payload };
      case CATEGORY_LIST_FAIL:
        return { loading: false, error: action.payload };
      case CATEGORY_LIST_RESET:
        return {};
      default:
        return state;
    }
  };
