import axios from "axios";
import { 
    POST_LIST_REQUEST,
    POST_LIST_SUCCESS,
    POST_LIST_FAIL,

    POST_DETAILS_REQUEST,
    POST_DETAILS_SUCCESS,
    POST_DETAILS_FAIL,

    POST_CREATE_COMMENT_REQUEST,
    POST_CREATE_COMMENT_SUCCESS,
    POST_CREATE_COMMENT_FAIL,

    POST_CREATE_LIKE_REQUEST,
    POST_CREATE_LIKE_SUCCESS,
    POST_CREATE_LIKE_FAIL,
    POST_CREATE_LIKE_RESET,

    // POST_CREATE_LIKE_UPDATE_REQUEST,
    // POST_CREATE_LIKE_UPDATE_SUCCESS,
    // POST_CREATE_LIKE_UPDATE_FAIL,
    // POST_CREATE_LIKE_UPDATE_RESET,

 } from '../constants/postConstants'
 import { toast } from "react-toastify";

 export const listPosts = () => async (dispatch) => {
    try {
      dispatch({ type: POST_LIST_REQUEST });
      const { data } = await axios.get(`/api/posts`);
      dispatch({
        type: POST_LIST_SUCCESS,
        payload: data,
      });
    } catch (error) {
      dispatch({
        type: POST_LIST_FAIL,
        payload:
          error.response && error.response.data.detail
            ? error.response.data.detail
            : error.detail,
      });
    }
  };


  // export const listPostsAfterLike = () => async (dispatch) => {
