import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
    Row,
    Col,
    Image,
    ListGroup,
    Button,
    Card,
    Form,
  } from "react-bootstrap";
import { Link, useParams, useNavigate } from "react-router-dom";
import Loader from "../components/Loader";
import Message from "../components/Message";
import Trending from '../components/Trending'
import { listPostDetails, createPostComment } from "../actions/postActions";
import Reactions from '../components/Reactions';
import { POST_CREATE_COMMENT_RESET } from '../constants/postConstants'
import moment from 'moment';


function PostScreen() {
    const { id } = useParams();
    const history = useNavigate();
    const dispatch = useDispatch();

    const [comment, setComment] = useState('');
    const [validated, setValidated] = useState(false);

    const postDetails = useSelector((state) => state.postDetails);
    const { loading, error, post } = postDetails;

    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;

    const postComments = useSelector((state) => state.postComments);
    const { loading: loadingPostComment, error:errorPostComment, success: successPostComment } = postComments;

    useEffect(() => {
        if(successPostComment){
            setComment('')
            dispatch({type: POST_CREATE_COMMENT_RESET})
          }

        dispatch(listPostDetails(id));
      }, [dispatch, id, successPostComment]);
    
      const submitHandler = (e) =>{
        const form = event.currentTarget;
            if (form.checkValidity() === false) {
            event.preventDefault();
            event.stopPropagation();
            }
        
            setValidated(true);
            e.preventDefault()
            dispatch(createPostComment(
            id,{
                comment
            }
            ))
        }

    return (
        <div>
            <Link to="/" className="btn btn-light my-3">
                <i className="fa fa-home"></i>
            </Link>
            {loading ? (
                <Loader/>
            ) : error ? (
                <Message variant="danger">{error}</Message>
            ) :(
                <div>
                    <Row>
                        <Col md={4}>
                            <Image src={post.image} alt={post.title} fluid />
                        </Col>
                        <Col md={5}>
                            <ListGroup variant="flush">
                                <ListGroup.Item>
                                    <p className="text-muted">
                                        <span className="badge bg-dark">
                                            {post.categories}
                                        </span>
                                    </p>
                                    <h3>{post.title}</h3>                                    
                                </ListGroup.Item>
                               

                                <ListGroup.Item>
                                    {post.description}
                                </ListGroup.Item>

                                <ListGroup.Item>
                                        <Reactions value={0} likes={post.likes.length} comment={post.comments.length} post={post._id}/>
                                </ListGroup.Item>

                            </ListGroup>
                        </Col>
                        <br/>
                        <Col md={3}>
                            <Trending/>
                        </Col>
                    </Row>

                    <br/>
                    <Row>
                        <Col md={6}>
                            <h3>Comments</h3><hr/>
                            {post.comments.length === 0 && <Message variant='info'>No Comments</Message>}
                            <Card >
                                {post.comments.map((comment) => (
                                    <Card.Body key={comment._id} className="commentList">
                                        <div className="d-flex justify-content-between mt-2 align-items-center">
                                            <strong>{comment.name}</strong> - <small>{moment(comment.createdAt).fromNow()}</small> 
                                        </div>                                      
                                        <p>{comment.comment}</p>
                                        <div className="action d-flex justify-content-between mt-2 align-items-center">
                                            <div className="reply px-4">
                                                <small>{comment.createdAt.substring(0,10)}</small>
                                            </div>
                                        </div>
                                    </Card.Body>
                                ))}
                            </Card>
                            <br/>
                            <ListGroup>
                                <ListGroup.Item>
                                    <h4>Write a Comment</h4>
                                    {loadingPostComment && <Loader/>}
                                    {successPostComment && <Message variant='success'>Comment Submitted</Message>}
                                    {errorPostComment  && <Message variant='danger'>{errorPostComment}</Message>}
                                
                                    {userInfo ? (
                                        <Form noValidate validated={validated} onSubmit={submitHandler}>
                                            
                                            <Form.Group controlId='comment'>
                                                <Form.Label>Comment</Form.Label>
                                                <Form.Control
                                                required
                                                as='textarea'
                                                row='5'
                                                value={comment}
                                                onChange={(e) => setComment(e.target.value)}
                                                >

                                                </Form.Control>
                                                <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                                            </Form.Group>
                                            <br/>
                                            <div className="btn-login">
                                                <Button
                                                    disabled={loadingPostComment}
                                                    type='submit'
                                                    variant = 'primary'
                                                > 
                                                    Submit
                                                </Button>
                                            </div>
                                           
                                        </Form>
                                    ) : (
                                        <Message variant='info'>Please <Link to='/login'>Login</Link> to write a comment</Message>
                                    )}

                                </ListGroup.Item>
                            </ListGroup>
                        </Col>
                    </Row>
                </div>
            )

            }
            
        </div>
    )
}

export default PostScreen
