import React, { useState, useEffect } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { Form, Button, Col, Row, FormControl } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../components/Loader";
import Message from "../components/Message";
import FormContainer from "../components/FormContainer";
import { login } from "../actions/userActions";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import API from "../components/Api";
import { createPost } from '../actions/postActions'
import axios from 'axios';

function CreatePostScreen() {
    const history = useNavigate();
    const location = useLocation();

    const dispatch = useDispatch();

    const postCreate = useSelector((state) => state.postCreate);
    const { error:errorPostCreate, loading:loadingPostCreate, success } = postCreate;


    const [formData, setFormData] = useState({
        title: "",
        description: "",
        image: "",
        category:"",
    });

    const [errors, setErrors] = useState({
        title: "",
        description: "",
        image: "",
        category:"",
    });

    const handleChange = ({ currentTarget: input }) => {
        let newData = { ...formData };
        newData[input.name] = input.value;
        setFormData(newData);
    };

    const handleImageChange = (e) => {
        let newData = { ...formData };
        newData["image"] = e.target.files[0];
        setFormData(newData);
    };


    const doSubmit = async (e) => {        
        e.preventDefault();
       const response = dispatch(createPost(
            formData
        ));

        if (response.status === 400) {
            setErrors(response.data);
        }
              
    };

    const userLogin = useSelector((state) => state.userLogin);
    const { error, loading, userInfo } = userLogin;

    const redirect = location.search ? location.search.split("=")[1] : "/";

    useEffect(() => {
        if (!userInfo) {
          history(redirect);
        }
      }, [history, userInfo, redirect]);

    return (
        <FormContainer>
            <ToastContainer/>
            <h4 className="text-center">Create Post</h4>
            <Form>
                <Row>
                    <Form.Group className="mb-3" controlId="titleInput">
                        <Form.Label>Title</Form.Label>
                        <Form.Control
                            type="text"
                            name="title"
                            placeholder="Type Your Post Title"
                            value={formData.title}
                            isInvalid={errors.title}
                            onChange={(e) => {
                                handleChange(e);
                            }}
                            maxLength={80}
                        />
                        {errors.title && (
                            <Form.Text className="alert-danger" tooltip>
                                {errors.title}
                            </Form.Text>
                        )}
                    </Form.Group>
                </Row>
                <Row>
                    <Form.Group controlId="formFile" className="mb-3">
                        <Form.Label>Image</Form.Label>
                        <Form.Control
                            type="file"
                            name="image"
                            accept="image/jpeg,image/png,image/gif"
                            onChange={(e) => {
                                handleImageChange(e);
                            }}
                        />
                        {errors.image_url && (
                            <Form.Text className="alert-danger" tooltip>
                                {errors.image}
                            </Form.Text>
                        )}
                    </Form.Group>
                </Row>
                <Form.Group className="mb-3" controlId="descriptionInput">
                    <Form.Label>Description</Form.Label>
                    <Form.Control
                        as="textarea"
                        rows={10}
                        name="description"
                        placeholder="Type Description"
                        value={formData.description}
                        isInvalid={errors.description}
                        onChange={(e) => {
                            handleChange(e);
                        }}
                    />
                    {errors.description && (
                        <Form.Text className="alert-danger" tooltip>
                            {errors.description}
                        </Form.Text>
                    )}
                </Form.Group>
                <div className="btn-login">
                    <Button
                        variant="primary"
                        type="submit"
                        onClick={(e) => doSubmit(e)}
                        >
                        Submit
                    </Button>
                </div>
                
        </Form>
        </FormContainer>
    )
}

export default CreatePostScreen
