import React, { useState, useEffect } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { Form, Card, Button, Col, Row, Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../components/Loader";
import Message from "../components/Message";
import FormContainer from "../components/FormContainer";
import { getUserDetails, updateUserProfile } from "../actions/userActions";
//this constant is used to reset the frontend value after the update Action have been successfully fired on, so it refreshes the frontend before it renders it
import { USER_UPDATE_PROFILE_RESET } from "../constants/userConstants";
import { listPosts } from "../actions/postActions";
import { LinkContainer } from "react-router-bootstrap";
import Trending from '../components/Trending'
import Reactions from '../components/Reactions'
import moment from 'moment';

function ProfileScreen() {
  const history = useNavigate();
  const location = useLocation();

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setconfirmPassword] = useState("");
  const [message, setMessage] = useState("");

  const dispatch = useDispatch();

  const userDetails = useSelector((state) => state.userDetails);
  const { error, loading, user } = userDetails;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const userUpdateProfile = useSelector((state) => state.userUpdateProfile);
  const { success } = userUpdateProfile;

  const postList = useSelector((state) => state.postList);
  const { error:errorPostList, loading:loadingPostList, success:successPostList, posts } = postList;

  const Allposts = posts.map(post => post);

  let numLikes = (0);
  let useLike;
  {userInfo ? (
    Allposts.post.likes.map((like) => {                                               
          return(
              <span key={like._id}>
                  {useLike = like.user == userInfo._id} 
                  {like.user == userInfo._id ? (numLikes = 1) : (numLikes)}                                                                  
              </span>               
          )
      }) 
  ):(
      <spa></spa>
  )}

  useEffect(() => {
    if (!userInfo) {
      history("/login");
    } else {
      
      if (!user || !user.name || success || userInfo._id !== user._id) {
        dispatch({ type: USER_UPDATE_PROFILE_RESET });
        dispatch(getUserDetails("profile"));
        dispatch(listPosts());
      }
      else {
        dispatch(listPosts());
        setName(user.name);
        setEmail(user.email);
      }
    }
  }, [dispatch, history, userInfo, user, success]);

  const submitHandler = (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setMessage("Passwords do not match");
    } else {
      dispatch(
        updateUserProfile({
          id: user._id,
          name: name,
          email: email,
          password: password,
        })
      );
      setMessage("");
    }
  };

  return (
    <div>
      <Row>
        <Col md={3}>
          <h2>User Profile</h2>
          {message && <Message variant="danger">{message}</Message>}
          {error && <Message variant="danger">{error}</Message>}
          {loading && <Loader />}
          <Form onSubmit={submitHandler}>
            <Form.Group controlId="name">
              <Form.Label>Username</Form.Label>
              <Form.Control
                required
                type="name"
                placeholder="Enter Username"
                value={name}
                onChange={(e) => setName(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="email">
              <Form.Label>Email Address</Form.Label>
              <Form.Control
                required
                type="email"
                placeholder="Enter Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group controlId="passwordConfrim">
              <Form.Label>Confirm Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Confrim Password"
                value={confirmPassword}
                onChange={(e) => setconfirmPassword(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <br />
            <div className="btn-login">
                <Button type="submit" variant="primary">
                Update
                </Button>
            </div>
            
          </Form>
        </Col>
        <Col md={9}>
          <h2>My Posts</h2>

          {loadingPostList ? (
            <Loader />
          ) : errorPostList ? (
            <Message variant="danger">{errorPostList}</Message>
          ) : (
                <div className="row">
                        <div className="col-md-8">                            
                            {posts.map((post) => (                      
                                <Card className="my-3 p-3 rounded">
                                    <Row>
                                        <Col key={post._id} sm={12} md={6} lg={4} xl={4}>
                                            <Link to={`/post/${post._id}`}>
                                                <Card.Img src={post.image} />
                                            </Link>
                                        </Col>
                                        <Col  sm={12} md={6} lg={8} xl={8}>
                                            <Card.Body>
                                                <Link to={`/post/${post._id}`}>
                                                    <Card.Title as="div" className="postTitle">
                                                        <strong>{post.title}</strong>
                                                    </Card.Title>
                                                </Link>
                                                <Card.Text as="p">
                                                    <div className="my-3">
                                                    {post.description.substring(0,200)} {post.description.length >= 20 && <Link to={`/post/${post._id}`}>...Read More...</Link> } 
                                                    </div>
                                                </Card.Text>
                                                <Card.Text as="div" className="category">
                                                    <button className="btn btn-outline-dark btn-sm mt-1 mb-1">
                                                        {post.categories}
                                                        
                                                        
                                                        {/* {post.comments.map((comment) => (                      
                                                            <p key={comment._id}>{comment._id}</p>                                                        
                                                        ))}  */}
                                                        
                                                    </button>                                   
                                                </Card.Text>
                                                <hr/>
                                                <div className="row">
                                                    <div className="col-md-6">
                                                        
                                                        <Reactions value={numLikes} likes={post.likes.length} comment={post.comments.length} post={post._id} user={post.user} />
                                                        
                                                    </div>
                                                    <div className="col-md-6">
                                                        <p className="text-muted">
                                                            <i className="fas fa-clock" style={{ color:'#cc6600' }}></i>                                        
                                                            {post.createdAt.substring(0,10)}
                                                            ({moment(post.createdAt).fromNow()})
                                                            
                                                        </p>                                        
                                                    </div>
                                                </div>
                                            </Card.Body>  
                                        </Col>     
                                    </Row>                                 
                                </Card>                                                     
                                ))}   
                                                                        
                        </div>
                    <div className="col-md-4">
                        <Trending/>
                    </div>
                </div>
          )}
        </Col>
      </Row>
    </div>
  );
}

export default ProfileScreen;
