import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { LinkContainer } from "react-router-bootstrap";
import { Table, Button } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../components/Loader";
import Message from "../components/Message";
import { listUsers, deleteUser } from "../actions/userActions";
import ConfirmationModal from '../components/ConfirmationModal'

function UserListScreen() {
    const history = useNavigate();
    const dispatch = useDispatch();

    const [displayConfirmationModal, setDisplayConfirmationModal] = useState(false);
    const [message, setMessage] = useState(null);
    const [uid, setUid] = useState(null);

    const [searchField, setSearchField] = useState("");
    const [searchShow, setSearchShow] = useState(false);

    const [currentPage, setCurrentPage] = useState(1);
    const [itemsPerPage, setItemsPerPage] = useState(5);

    const [pageNumberLimit, setPageNumberLimit] = useState(5);
    const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(5);
    const [minPageNumberLimit, setminPageNumberLimit] = useState(0);


    const userList = useSelector((state) => state.userList);
    const { loading, error, users } = userList;

    const userLogin = useSelector((state) => state.userLogin);
    const { userInfo } = userLogin;

    const userDelete = useSelector((state) => state.userDelete);
    const { success: successDelete } = userDelete;

    const showDeleteModal = (uid) => {
        setUid(uid);
        setMessage(`Are you sure you want to delete this USER '${users.find((x) => x._id === uid).username}' ? `);

        setDisplayConfirmationModal(true)
        };

    const hideConfirmationModal = () => {
        setDisplayConfirmationModal(false);
        
    }
    const handleClick= (event) =>{
      setCurrentPage(Number(event.target.id));
  }
  const handleNextBtn = () => {
      setCurrentPage(currentPage + 1);

      if(currentPage + 1 > maxPageNumberLimit){
          setmaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
          setminPageNumberLimit(minPageNumberLimit + pageNumberLimit);
      }
  }
  const handlePrevBtn = () => {
      setCurrentPage(currentPage - 1);

      if((currentPage - 1) % pageNumberLimit == 0 ){
          setmaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
          setminPageNumberLimit(minPageNumberLimit - pageNumberLimit);
      }
  }

  const pages = [];

  for(let i = 1; i<=Math.ceil(Number(users).length/itemsPerPage); i++){
      pages.push(i);
  }

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = users.slice(indexOfFirstItem, indexOfLastItem);

  const renderPageNumbers = pages.map((number) => {
      if(number < maxPageNumberLimit + 1 && number > minPageNumberLimit){
          return(
              <li 
                  className={`page-link ${currentPage === number ? "active" : null}`}
                  key={number} 
                  id={number} 
                  onClick={handleClick}
              >
                {number} 
              </li>
          )
      }else{
          return null;
      }
      
  })

  let pageIncrementBtn = null;
  if(pages.length > maxPageNumberLimit){
      pageIncrementBtn =  <li onClick={handleNextBtn}>
                              <button 
                                  class="page-link"  
                                  aria-label="Next"
                                  disabled={currentPage == pages[pages.length - 1] ? true : false}
                                  >
                                  <span aria-hidden="true">&hellip;</span>
                              </button>
                          </li>
  }

  let pageDecrementBtn = null;
  if(pages.length > maxPageNumberLimit){
      pageDecrementBtn =  <li onClick={handlePrevBtn}>
                                  <button class="page-link" 
                                          aria-label="Previous"
                                          disabled={currentPage == pages[0] ? true : false}
                                          >
                                      <span aria-hidden="true">&hellip;</span>
                                  </button>
                              </li>
  }
  const filteredUsers = users.filter(
      x => {
        return (
          x
          .username
          .toLowerCase()
          .includes(searchField.toLowerCase()) ||
          x
          .email
          .toLowerCase()
          .includes(searchField.toLowerCase())
        );
      }
    );
    const handleSearch = e => {
      setSearchField(e.target.value);
      if(e.target.value===""){
        setSearchShow(false);
      }
      else {
        setSearchShow(true);
      }
    };

    useEffect(() => {
            if (userInfo && userInfo.isAdmin) {
            dispatch(listUsers());
            } else {
            history("/login");
            }
        }, [dispatch, history, successDelete]);

    const deleteHandler = (uid) => {
            dispatch(deleteUser(uid));
            setDisplayConfirmationModal(false);
        };

  return (
    <div>
       <div className="row">
          <div className="col-md-4">
              <h1>All USERS</h1>
          </div>
          <div className="col-md-8">
              <div className="search">
                  <i className="fa fa-search"></i>
                  <input 
                      className="me-2 form-control"
                      type = "search" 
                      placeholder = "Search Users" 
                      onChange = {handleSearch}
                  />
              </div>
              
          </div>
        </div>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Table striped bordered hover responsive className="table-sm">
          <thead>
            <tr>
              <th>NAME</th>
              <th>EMAIL</th>
              <th>ADMIN STATUS</th>
              <th>ACTIONS</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>
                  {user.isAdmin ? (
                    <i className="fas fa-check" style={{ color: "green" }}></i>
                  ) : (
                    <i className="fas fa-times" style={{ color: "red" }}></i>
                  )}
                </td>
                <td>
                  <LinkContainer to={`/admin/user/${user._id}/edit`}>
                    <Button className="btn-sm" variant="light">
                      <i className="fas fa-edit"></i>
                    </Button>
                  </LinkContainer>
                  <Button
                    className="btn-sm"
                    variant="danger"
                    onClick={() => showDeleteModal(user._id)}
                  >
                    <i className="fas fa-trash"></i>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
      <ConfirmationModal variantBtnDel={"danger"} ModalTitle={"Confirmation"} showModal={displayConfirmationModal} confirmModal={deleteHandler} hideModal={hideConfirmationModal}  uid={uid} message={message}  />
    </div>
  );
}

export default UserListScreen;
